mapper.select:
SELECT  member_id, member_name, member_mail, member_gender, member_tell, member_area
FROM member 
WHERE member_id = #{id}

mapper.update:
UPDATE member SET 
member_id = #{memberId}, member_name = #{memberName}, member_pw = #{memberPw}, member_mail = #{memberMail},
 member_tell = #{memberTell}, member_area = #{memberArea}, mail_confrim = #{mailConfrim}
 WHERE  member_id = #{memberId}
